mapper.select:
select t.id as id, t.version as version, t.jmsku_code as jmskuCode, t.bar_code as barCode, t.qty as qty, t.unit_price as unitPrice, t.total_actual as totalActual, t.so_log_id as soLog, t.sku_combo_remark as skuComboRemark, t.extention_code as extentionCode, t.list_price as listPrice, t.md_price as mdPrice, t.coupon_code as couponCode, t.discount_fee as discountFee, t.line_total as lineTotal, t.line_unit_price as lineUnitPrice, t.final_total_actual as finalTotalActual, t.final_unit_price as finalUnitPrice, t.total_point_used as totalPointUsed, t.ext_prop1 as extProp1, t.platform_line_id as platformLineId, t.outer_point_value as outerPointValue, t.inner_point_value as innerPointValue, t.other_vc as otherVc, t.platform_source as platformSource, t.platform_sku_name as platformSkuName, t.warranty_months as warrantyMonths, t.platform_wh_code as platformWhCode, t.is_prezzie as isPrezzie, t.EST_CON_TIME as estConTime, t.PIC_PATH as picPath, t.SHOP_ID as shopId, t.SUB_ORDER_TAX_FEE as subOrderTaxFee, t.ASSEMBLY_RELA as assemblyRela, t.PART_MJZ_DISCOUNT as partMjzDiscount, t.TOTAL_FEE as totalFee, t.targetCode as targetCode, t.TARGET_CODE as targetCode, t.TARGET_TYPE as targetType, t.ALLOCATION_CODE as allocationCode, t.FQG_NUM as fqgNum, t.IS_FQG_S_FEE as isFqgSFee, t.ALLOCATION_TYPE as allocationType, t.PLATFORM_SKU_ID as platformSkuId, t.COUPON_FEE as couponFee from t_mq_so_line_log t
and t.SO_LOG_ID=#{soLog}
AND t.SHOP_ID = #{shopId}
